import React, { useEffect, useState } from "react";
import CardItem from "../CardItem";
import Pagination from "../Pagination";
import queryString from 'query-string'
const Card = () => {
  const [product, setProduct] = useState();
  const [pagination, setPagination] = useState({
    page : 1,
    limit :10,
    _totalRows : 11
  })
  const [filter, setFilter] = useState({
    limit : 10,
    page :1
  })
  useEffect(() => {
    async function API_heaphone() {
      const params = queryString.stringify(filter)
      const url_API =`https://headphoneapi.herokuapp.com/api/products?${par}`;
      const response = await fetch(url_API);
      const result = await response.json();
      console.log({ result });
      const data = result;
      setProduct(data);
    }
    API_heaphone();
  }, [filter]);
  const HandelPagechange = (newPages) =>{
    console.log(newPages);
  }
  return (
    <section className="text-gray-600 body-font">
      <div className="container px-5 py-24 mx-auto">
        <CardItem cart={product}></CardItem>
      </div>
      <Pagination pagination={pagination} onPageChange={HandelPagechange}></Pagination>
    </section>
  );
};

export default Card;
