import React, { useState } from "react";
import PropTypes from "prop-types";

FormAddTodo.propTypes = {
  onSubmit: PropTypes.func,
};
FormAddTodo.defaultProps = {
  onSubmit: null,
};

function FormAddTodo(props) {
  const { onSubmit } = props;
  const [value, setValue] = useState("");
  const handleChangeValue = (e) => {
    console.log(e.target.value);
    setValue(e.target.value);
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    if(!onSubmit) return
    const formValues = {
        title : value
    }
    onSubmit(formValues)
    setValue('')
  };
  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={value} onChange={handleChangeValue} />
      <div class="my-5 text-sm">
                        <label for="username" class="block text-black">Username</label>
                        <input type="text" autofocus id="username" class="rounded-sm px-4 py-3 mt-3 focus:outline-none bg-gray-100 w-full" placeholder="Username" />
                    </div>
    </form>
  );
}

export default FormAddTodo;
