import React, {useState} from 'react'
import './style.scss'
const ColorBox = () => {
    const [color, setcolor] = useState("deeppink")
    const getRandomColor = ()=>{
        const colorRandom = ['green','yellow','blue']
        const randomColor  = Math.trunc(Math.random)
    }
    const handelChangeColor = () =>{
        const newColor = getRandomColor()
        setcolor(newColor)
    }
    return (
        <div className="box-color" style={{backgroundColor:color}} onClick={handelChangeColor}>
        <h2>color box</h2>
        </div>
    )
}

export default ColorBox
